import React, { useState, useEffect } from "react";
import paasCal from "../data/paas-cal.json";
import PMT from "../utils/pmt";

import Loading from "./loading/Loading";
import Step1 from "./steps/Step1";
import Step2 from "./steps/Step2";
import Step3 from "./steps/Step3";
import Step4 from "./steps/Step4";
import Step5 from "./steps/Step5";
import Step6 from "./steps/Step6";
import Step7 from "./steps/Step7";
import Step8 from "./steps/Step8";

import "../scss/paas-calculator.scss";

const PaasCalculator = () => {
  const [whichStep, setWhichStep] = useState(8);

  //#region STEPS
  const renderSteps = (whichStep) => {
    switch (whichStep) {
      case 2:
        return (
          <Step2
            {...{
              whichStep,
              setWhichStep,
              customerInputs,
              setPADDRegion,
              setCustomerInputs,
            }}
          />
        );
      case 3:
        return (
          <Step3
            {...{
              whichStep,
              setWhichStep,
              customerInputs,
              setCustomerInputs,
            }}
          />
        );
      case 4:
        return (
          <Step4
            {...{
              whichStep,
              setWhichStep,
              customerInputs,
              setPADDRegion,
              setCustomerInputs,
            }}
          />
        );
      case 5:
        return (
          <Step5
            {...{
              whichStep,
              setWhichStep,
              setCustomerInputs,
              setPADDRegion,
              customerInputs,
            }}
          />
        );
      case 6:
        return (
          <Step6
            {...{
              whichStep,
              setWhichStep,
              customerInputs,
              setPADDRegion,
              setCustomerInputs,
            }}
          />
        );
      case 7:
        return <Step7 {...{ whichStep, setWhichStep }} />;
      case 8:
        return (
          <Step8
            {...{
              pricing,
              totalMonthlyPaaSPowerCost,
              totalMonthlyPaaSMaintenanceCost,
              requiredEquipment,
              breakeven,
              reductionFromNGBRResale,
              gasZTR,
              cashPurchase,
              NGBRWithFlexiblePowerProgram,
              numberOfMaintenanceJobsPerYear,
              averageTotalTimeForServicing,
              monthlyPaaSPowerCostNGBR,
              customerInputs,
              monthlyFuelCostPerZTR,
              monthlyMaintenanceCostPerNGBR,
              monthlyMaintenanceCostPerZTR,
              enviromentalBenefits,
              poundsOfCO2Avoided,
              whichStep,
              setPADDRegion,
              setCustomerInputs,
            }}
          />
        );

      default:
        return (
          <Step1
            {...{
              whichStep,
              setWhichStep,
              customerInputs,
              setPADDRegion,
              setCustomerInputs,
            }}
          />
        );
    }
  };
  //#endregion


  return (
    <>
      {loading === true ? (
        <Loading {...{ loading }} />
      ) : (
        <section
          className="coh-container section coh-style-section"
          data-gs-background-color="light-grey"
        >
          <div className="section-inner steps">
            <div className={`${whichStep === 8 ? "" : "coh-container-boxed"}`}>
              {renderSteps(whichStep)}
            </div>
          </div>
        </section>
      )}
    </>
  );
};

export default PaasCalculator;
