import { useState } from "react";

import ButtonEdit from "../elements/ButtonEdit";

const GasZTRPrice = ({
  customerInputs,
  setCustomerInputs,
  inStep,
  whichStep,
  setWhichStep,
}) => {
  const [editThis, setEditThis] = useState(false);
  const handleChange = (e) => {
    setCustomerInputs({
      ...customerInputs,
      gasZTRPrice: e.target.value,
    });
  };

  return (
    <>
      <label>
        What's the price of a new, commercial-grade gas ZTR?
        <ButtonEdit {...{ whichStep, setEditThis }} />
      </label>
      {inStep || editThis ? (
        <span className="input">
          <output htmlFor="gasZTRPrice" className="slider-output">
            {customerInputs.gasZTRPrice}
          </output>
          <input
            type="range"
            min="9000"
            max="16000"
            step="10"
            value={customerInputs.gasZTRPrice}
            onChange={(e) => {
              handleChange(e);
            }}
            name="gasZTRPrice"
            id="gasZTRPrice"
            list="gasZTRPrice-ticks"
          />
        </span>
      ) : (
        <>
          <span className="coh-heading subtitle coh-style-h2---display">
            {customerInputs.gasZTRPrice}
          </span>
          {whichStep !== 8 && (
            <>
              <button onClick={(e) => setWhichStep(2)} className="go-button">
                GO TO STEP 2
              </button>
              <span className="circle">2</span>
            </>
          )}{" "}
        </>
      )}
    </>
  );
};

export default GasZTRPrice;
