import Button from "../elements/Button";

const Buttons = ({ whichStep, setWhichStep }) => {
  return (
    <div className="buttons">
      {whichStep > 1 ? (
        <Button
          {...{
            setWhichStep,
            buttonText: "Previous",
            whichStep: whichStep - 1,
          }}
        />
      ) : (
       <li><div></div></li> 
      )}
      <li></li>
      <Button
        {...{
          setWhichStep,
          buttonText: "Skip to results »",
          whichStep: 8,
          addClass: "center",
        }}
      />
      <Button
        {...{
          setWhichStep,
          buttonText: "Next",
          whichStep: whichStep + 1,
          addClass: "right",
        }}
      />
    </div>
  );
};

export default Buttons;
