import { useState } from "react";

import RangeTicks from "../elements/RangeTicks";
import ButtonEdit from "../elements/ButtonEdit";

const NumberNGBRUnits = ({
  customerInputs,
  setCustomerInputs,
  inStep,
  whichStep,
}) => {
  const [editThis, setEditThis] = useState(false);
  const handleChange = (e) => {
    setCustomerInputs({
      ...customerInputs,
      daysMowedPerWeek: e.target.value,
    });
  };

  const handleClick = (e) => {
    setCustomerInputs({
      ...customerInputs,
      daysMowedPerWeek: e.target.value,
    });
  };

  return (
    <>
      <label id="numberNGBRUnits">
        How many NGBRs do you plan to operate?
        <ButtonEdit {...{ whichStep, setEditThis }} />
      </label>
      {inStep || editThis ? (
        <span className="input">
          <input
            type="range"
            min="1"
            max="10"
            step="1"
            value={customerInputs.numberNGBRUnits}
            onChange={handleChange}
            name="numberNGBRUnits"
            list="numberNGBRUnits-ticks"
          />
          <RangeTicks
            handleClick={handleClick}
            sliderID="numberNGBRUnits"
            startTick={1}
            currentTick={customerInputs.numberNGBRUnits}
            endTick={10}
          />
        </span>
      ) : (
        <div>{customerInputs.numberNGBRUnits}</div>
      )}
    </>
  );
};

export default NumberNGBRUnits;
