import { useState } from "react";

import ButtonEdit from "../elements/ButtonEdit";

const GasZTRPrice = ({
  customerInputs,
  setCustomerInputs,
  handleChange,
  inStep,
  whichStep,
}) => {
  const [editThis, setEditThis] = useState(false);

  const handleChangeNew = (e) => {
    setCustomerInputs({
      ...customerInputs,
      gasZTRPrice: e.target.value,
    });
  };
  console.log("handleChange", handleChange);

  return (
    <>
      <label>
        What's the price of a new, commercial-grade gas ZTR?
        <ButtonEdit {...{ whichStep, setEditThis }} />
      </label>
      {inStep || editThis ? (
        <span className="input">
          <output htmlFor="gasZTRPrice" className="slider-output">
            {customerInputs.gasZTRPrice}
          </output>
          <input
            type="range"
            min="9000"
            max="16000"
            step="10"
            value={customerInputs.gasZTRPrice}
            onChange={(e) => {
              handleChangeNew(e);
            }}
            name="gasZTRPrice"
            id="gasZTRPrice"
            list="gasZTRPrice-ticks"
          />
        </span>
      ) : (
        <div>{customerInputs.gasZTRPrice}</div>
      )}
    </>
  );
};

export default GasZTRPrice;
