const Button = ({
  whichStep,
  setWhichStep,
  buttonText,
  addClass,
  onStepClick,
}) => {
  const createFullPostMarkup = () => {
    return {
      __html: `${buttonText}`,
    };
  };

  return (
    <button
      className={`coh-link coh-style-button-action ${addClass}`}
      onClick={(e) => {
        setWhichStep(whichStep);
        onStepClick(whichStep);
      }}
    >
      <div dangerouslySetInnerHTML={createFullPostMarkup()} />
    </button>
  );
};

export default Button;
