import React from "react";
import State from "./State";
import GasZTRPrice from "./GasZTRPrice";
import DaysMowedPerWeek from "./DaysMowedPerWeek";
import NumberNGBRUnits from "./NumberNGBRUnits";
import MowingHours from "./MowingHours";
import LengthMowingSeason from "./LengthMowingSeason";

const Inputs = ({ setPADDRegion, customerInputs, setCustomerInputs }) => {
  const handleChange = (e) => {
    let inputValue;
    if (isNaN(e.target.value)) {
      inputValue = e.target.value;
    } else {
      inputValue = Number(e.target.value);
    }

    // if (
    //   e.target.type === "range" &&
    //   e.target.previousElementSibling &&
    //   e.target.previousElementSibling.type === "output"
    // ) {
    //   //setMoveOutput(setLeftPos(e.target));
    // }

    setCustomerInputs({
      ...customerInputs,
      [e.target.getAttribute("name")]: inputValue,
    });
  };

  const handleClick = (e) => {
    setCustomerInputs({
      ...customerInputs,
      [e.target.parentNode.previousSibling.getAttribute("name")]:
        e.target.value,
    });
  };

  const buttonClick = (e) => {
    e.preventDefault();
    document.querySelectorAll("button").forEach(function (button) {
      button.classList.remove("active");
    });
    e.target.classList.add("active");
  };

  return (
    <div className="main container-inputs">
      <ul className="inputs" id="inputs">
        <li className="column states">
          <State {...{ customerInputs, handleChange, setPADDRegion }} />
        </li>
        <li className="column range with-output">
          <GasZTRPrice {...{ customerInputs, handleChange }} />
        </li>
        <li className="column range">
          <DaysMowedPerWeek
            {...{ customerInputs, handleChange, handleClick }}
          />
        </li>
        <li className="column range">
          <NumberNGBRUnits {...{ customerInputs, handleChange, handleClick }} />
        </li>
        <li className="column">
          <MowingHours {...{ handleChange, buttonClick }} />
        </li>
        <li className="column range">
          <LengthMowingSeason
            {...{ handleChange, handleClick, customerInputs }}
          />
        </li>
      </ul>
    </div>
  );
};

export default Inputs;
