import { useState } from "react";

import statesJSON from "../../data/states.json";
import ButtonEdit from "../elements/ButtonEdit";

const State = ({
  customerInputs,
  setCustomerInputs,
  setPADDRegion,
  inStep,
  whichStep,
}) => {
  let states = JSON.parse(JSON.stringify(statesJSON));
  const [editThis, setEditThis] = useState(false);

  const handleChange = (e) => {
    //  setCustomerInputs({
    //    ...customerInputs,
    //    gasZTRPrice: e.target.value,
    //  });
  };

  return (
    <>
      <label id="location">
        Which state do you operate in?{" "}
        <ButtonEdit {...{ whichStep, setEditThis }} />
      </label>
      {inStep || editThis ? (
        <select
          htmlFor="location"
          value={customerInputs.location}
          onChange={(e) => {
            handleChange(e);
            const newState = states.find(
              (state) => state.abbr === e.target.value
            );
            setPADDRegion(`PADD${newState.padd}`);
          }}
          disabled
        >
          {states.map((state) => {
            return (
              <option key={state.abbr} value={state.abbr}>
                {state.name} - PADD {state.padd}
              </option>
            );
          })}
        </select>
      ) : (
        <div>
          <span c>{customerInputs.location}</span>
          <span className="circle">1</span>
        </div>
      )}
    </>
  );
};

export default State;
