const DaysMowedPerWeek = ({ customerInputs, handleChange }) => {
  return (
    <>
      <span>
        <label id="daysMowedPerWeek">How many days do you mow per week?</label>
      </span>
      <span className="input">
        <input
          type="range"
          min="4"
          max="7"
          step="1"
          value={customerInputs.daysMowedPerWeek}
          onChange={handleChange}
          name="daysMowedPerWeek"
          list="daysMowedPerWeek-ticks"
        />
        <RangeTicks
          handleClick={handleClick}
          sliderID="daysMowedPerWeek"
          startTick={4}
          endTick={7}
          currentTick={customerInputs.daysMowedPerWeek}
        />
      </span>
    </>
  );
};

export default DaysMowedPerWeek;
