import { useState } from "react";

const MowingHours = ({
  customerInputs,
  setCustomerInputs,
  inStep,
  whichStep,
}) => {
  const [editThis, setEditThis] = useState(false);
  const handleChange = (e) => {
    setCustomerInputs({
      ...customerInputs,
      mowingHours: e.target.value,
    });
  };


  const buttonClick = (e) => {
    e.preventDefault();
    document.querySelectorAll("button").forEach(function (button) {
      button.classList.remove("active");
    });
    e.target.classList.add("active");
  };

  return (
    <>
      <label id="mowingHours">How many hours do you mow per day?</label>
      {inStep ? (
        <span>
          <ul className="buttons">
            <li>
              <button
                className="coh-link coh-style-button-action coh-style-branded-button"
                name="mowingHours"
                value="3"
                onClick={(e) => {
                  buttonClick(e);
                  handleChange(e);
                }}
              >
                &#60;4
              </button>
            </li>
            <li>
              <button
                className="coh-link coh-style-button-action coh-style-branded-button"
                name="mowingHours"
                value="5"
                onClick={(e) => {
                  buttonClick(e);
                  handleChange(e);
                }}
              >
                4-6
              </button>
            </li>
            <li>
              <button
                className="coh-link coh-style-button-action coh-style-branded-button active"
                name="mowingHours"
                value="7"
                onClick={(e) => {
                  buttonClick(e);
                  handleChange(e);
                }}
              >
                6-8
              </button>
            </li>
            <li>
              <button
                className="coh-link coh-style-button-action coh-style-branded-button"
                name="mowingHours"
                value="9"
                onClick={(e) => {
                  buttonClick(e);
                  handleChange(e);
                }}
              >
                8+
              </button>
            </li>
          </ul>
        </span>
      ) : (
        <div>{customerInputs.mowingHours}</div>
      )}
    </>
  );
};

export default MowingHours;
