const MowingHours = ({
  customerInputs,
  handleChange,
  buttonClick,
  edit = 0,
}) => {
  return (
    <>
      <label id="mowingHours">How many hours do you mow per day?</label>
      {edit === 1 ? (
        <span>
          <ul className="buttons">
            <li>
              <button
              c
                name="mowingHours"
                value="3"
                onClick={(e) => {
                  buttonClick(e);
                  handleChange(e);
                }}
              >
                &#60;4
              </button>
            </li>
            <li>
              <button
                name="mowingHours"
                value="5"
                onClick={(e) => {
                  buttonClick(e);
                  handleChange(e);
                }}
              >
                4-6
              </button>
            </li>
            <li>
              <button
                className="active"
                name="mowingHours"
                value="7"
                onClick={(e) => {
                  buttonClick(e);
                  handleChange(e);
                }}
              >
                6-8
              </button>
            </li>
            <li>
              <button
                name="mowingHours"
                value="9"
                onClick={(e) => {
                  buttonClick(e);
                  handleChange(e);
                }}
              >
                8+
              </button>
            </li>
          </ul>
        </span>
      ) : (
        <div>{customerInputs.mowingHours}</div>
      )}
    </>
  );
};

export default MowingHours;
