import { useState } from "react";

import ButtonEdit from "../elements/ButtonEdit";

const MowingHours = ({
  customerInputs,
  setCustomerInputs,
  inStep,
  whichStep,
  setWhichStep,
}) => {
  const [editThis, setEditThis] = useState(false);
  const handleChange = (e) => {
    setCustomerInputs({
      ...customerInputs,
      mowingHours: e.target.value,
    });
  };

  const buttonClick = (e) => {
    e.preventDefault();
    document.querySelectorAll("button").forEach(function (button) {
      button.classList.remove("active");
    });
    e.target.classList.add("active");
  };

  return (
    <>
      <label id="mowingHours">
        {inStep ? "How many hours do you mow per day?" : "Mowing Hours"}
        <ButtonEdit {...{ whichStep, setEditThis }} />
      </label>
      {inStep || editThis ? (
        <span>
          <ul className="buttons">
            <li>
              <button
                className={`step-button coh-style-branded-button ${customerInputs.mowingHours}`}
                name="mowingHours"
                value="3"
                onClick={(e) => {
                  buttonClick(e);
                  handleChange(e);
                }}
              >
                &#60;4
              </button>
            </li>
            <li>
              <button
                className="step-button coh-style-branded-button"
                name="mowingHours"
                value="5"
                onClick={(e) => {
                  buttonClick(e);
                  handleChange(e);
                }}
              >
                4-6
              </button>
            </li>
            <li>
              <button
                className="step-button coh-style-branded-button"
                name="mowingHours"
                value="7"
                onClick={(e) => {
                  buttonClick(e);
                  handleChange(e);
                }}
              >
                6-8
              </button>
            </li>
            <li>
              <button
                className="step-button coh-style-branded-button"
                name="mowingHours"
                value="9"
                onClick={(e) => {
                  buttonClick(e);
                  handleChange(e);
                }}
              >
                8+
              </button>
            </li>
          </ul>
        </span>
      ) : (
        <>
          <span className="coh-heading subtitle coh-style-h2---display">
            {customerInputs.mowingHours}
          </span>
          {whichStep !== 8 && (
            <>
              <button onClick={(e) => setWhichStep(5)} className="go-button">
                GO TO STEP 5
              </button>
              <span className="circle">5</span>
            </>
          )}{" "}
        </>
      )}
    </>
  );
};

export default MowingHours;
