import { useState, useEffect } from "react";

import statesJSON from "../../data/states.json";
let states = JSON.parse(JSON.stringify(statesJSON));

const State = ({ customerInputs, handleChange, setPADDRegion, edit = 0 }) => {
  const [editThis, setEditThis] = useState(0);

  return (
    <>
      <label id="location">Which state do you operate in? <button onc>Edit</button></label>
      {edit || editThis ? (
        <select
          htmlFor="location"
          value={customerInputs.location}
          onChange={(e) => {
            handleChange(e);
            const newState = states.find(
              (state) => state.abbr === e.target.value
            );
            setPADDRegion(`PADD${newState.padd}`);
          }}
          disabled
        >
          {states.map((state) => {
            return (
              <option key={state.abbr} value={state.abbr}>
                {state.name} - PADD {state.padd}
              </option>
            );
          })}
        </select>
      ) : (
        <div>{customerInputs.location}</div>
      )}
    </>
  );
};

export default State;
