import statesJSON from "../../data/states.json";
import ButtonEdit from "../elements/ButtonEdit";
//import RangeTicks from "../elements/RangeTicks";

const State = ({
  setDuoarea,
  customerInputs,
  setCustomerInputs,
  inStep,
  whichStep,
  setWhichStep,
  editThis,
  setEditThis,
}) => {
  let states = JSON.parse(JSON.stringify(statesJSON));

  const handleChange = (e) => {
    // console.log("handleChange", e.target.value);
    setCustomerInputs({
      ...customerInputs,
      location: e.target.value,
    });
    const newState = states.find((state) => state.abbr === e.target.value);
    //console.log("newState", newState);
    setDuoarea(newState.duoarea);
    setEditThis({
      gasZTRPrice: false,
      daysMowedPerWeek: false,
      numberNGBRUnits: false,
      mowingHours: false,
      lengthMowingSeason: false,
      state: false,
    });
  };

  // const handleClick = (e) => {
  //   setCustomerInputs({
  //     ...customerInputs,
  //     gasZTRPrice: e.target.value,
  //   });
  // };

  const handleEdit = (e) => {
    setEditThis({
      gasZTRPrice: false,
      daysMowedPerWeek: false,
      numberNGBRUnits: false,
      mowingHours: false,
      lengthMowingSeason: false,
      state: !editThis.state,
    });
  };

  return (
    <>
      <label id="location">
        {inStep ? "Which state do you operate in?" : "State"}
        <ButtonEdit {...{ whichStep }} onClick={handleEdit} />
      </label>
      {inStep || editThis.state ? (
        <span className="input">
          <select
            name="location"
            id="location"
            value={customerInputs.location}
            onChange={handleChange}
          >
            {states.map((state) => {
              return (
                <option key={state.abbr} value={state.abbr}>
                  {state.name} ({state.duoarea})
                </option>
              );
            })}
          </select>

          {/* <input
            type="range"
            min="0"
            max="16000"
            step=".1"
            value={customerInputs.gasZTRPrice}
            onChange={(e) => {
              handleChange(e);
            }}
            onMouseUp={(e) => {
              setEditThis({
                state: false,
                daysMowedPerWeek: false,
                numberNGBRUnits: false,
                mowingHours: false,
                lengthMowingSeason: false,
                gasZTRPrice: false,
              });
            }}
            name="gasZTRPrice"
            id="gasZTRPrice"
            list="gasZTRPrice-ticks"
          /> */}
          {/* <RangeTicks
            handleClick={handleClick}
            sliderID="daysMowedPerWeek"
            startTick={9000}
            endTick={16000}
            currentTick={customerInputs.daysMowedPerWeek}
            skipTicks={true}
          /> */}
        </span>
      ) : (
        <>
          <span className="coh-heading subtitle coh-style-h2---display">
            {customerInputs.location}
          </span>
          {whichStep !== 8 && (
            <>
              <button onClick={(e) => setWhichStep(1)} className="go-button">
                GO TO STEP 1
              </button>
              <span className="circle">1</span>
            </>
          )}
        </>
      )}
    </>
  );
};

export default State;
