const Step1 = ({
  whichStep,
  setWhichStep,
  customerInputs,
  setDuoarea,
  setCustomerInputs,
  pricing,
  requiredEquipment,
  editThis,
  setEditThis,
}) => {
  // const [editThis, setEditThis] = useState({
