                  />
                </li>
              </ul>
            </div>
            <Buttons {...{ whichStep, setWhichStep }} />
            <Inputs
              {...{
                setDuoarea,
                customerInputs,
                setCustomerInputs,
                whichStep,
                setWhichStep,
                editThis,
                setEditThis,
              }}
            />
          </div>
        </div>
      </section>

      <section className="container-outputs">
        <OutputsPricing {...{ pricing, requiredEquipment }} />
      </section>
    </>
  );
};

export default Step1;
