      breakevenFPPNoResale: (
        (totalNGBRCostBare - totalGasZTRPurchasePrice) /
        savingsWithFPP
      ).toFixed(4),
      breakeven3yearFinancingNoResale: (
        (totalNGBRCostBare - totalGasZTRPurchasePrice) /
        savingsWith3yearFinancing
      ).toFixed(4),
      breakeven4yearFinancingNoResale: (
        (totalNGBRCostBare - totalGasZTRPurchasePrice) /
        savingsWith4yearFinancing
      ).toFixed(4),
    });
  }, [
