import { useState } from "react";

import RangeTicks from "../elements/RangeTicks";
import ButtonEdit from "../elements/ButtonEdit";

const LengthMowingSeason = ({
  customerInputs,
  setCustomerInputs,
  inStep,
  whichStep,
  setWhichStep,
}) => {
  const [editThis, setEditThis] = useState(false);
  const handleChange = (e) => {
    setCustomerInputs({
      ...customerInputs,
      lengthMowingSeason: e.target.value,
    });
  };

  const handleClick = (e) => {
    setCustomerInputs({
      ...customerInputs,
      lengthMowingSeason: e.target.value,
    });
  };

  const goInput = (e) => {
    this.setWhichStep(6);
  };

  return (
    <>
      <label id="lengthMowingSeason">
        How long is your mowing season per year?
        <ButtonEdit {...{ whichStep, setEditThis }} />
      </label>
      {inStep || editThis ? (
        <span className="input">
          <input
            type="range"
            min="6"
            max="12"
            step="1"
            value={customerInputs.lengthMowingSeason}
            onChange={handleChange}
            name="lengthMowingSeason"
            list="lengthMowingSeason-ticks"
          />
          <RangeTicks
            handleClick={handleClick}
            sliderID="lengthMowingSeason"
            startTick={6}
            endTick={12}
            currentTick={customerInputs.lengthMowingSeason}
          />
        </span>
      ) : (
        <>
          <span className="coh-heading subtitle coh-style-h2---display">
            {customerInputs.lengthMowingSeason}
          </span>
          {whichStep !== 8 && <span className="circle">6</span>}
          {whichStep !== 8 && (
            <button onClick={goInput} className="go-button">
              GO
            </button>
          )}
        </>
      )}
    </>
  );
};

export default LengthMowingSeason;
