import Buttons from "../elements/Buttons";
import State from "../inputs/State";
import Inputs from "../inputs/Inputs";

const Step1 = ({
  whichStep,
  setWhichStep,
  customerInputs,
  setPADDRegion,
  setCustomerInputs,
}) => {
  return (
    <div className="main container-inputs">
      <div className="coh-container text-main-container">
        <h2>Step1</h2>

          <div className="column">
            <State
              {...{
                customerInputs,
                setPADDRegion,
                inStep: true,
                whichStep,
              }}
            />
          </div>

      </div>
      <Buttons {...{ whichStep, setWhichStep }} />
      <Inputs
        {...{
          setPADDRegion,
          customerInputs,
          setCustomerInputs,
          whichStep,
          setWhichStep,
        }}
      />
    </div>
  );
};

export default Step1;
